    setTheTheme: function () {
        try {
            console.log('In bwAuthentication.js.setTheTheme(): ' + this.options.workflowAppTheme);
            //alert('In bwAuthentication.js.setTheTheme(): ' + this.options.workflowAppTheme);
            debugger;
            //
            // This is only called from 1 place, bwAuthentication.js.securityTrimTheLeftMenuButtons().
            //

            if (!this.options.workflowAppTheme) {
                console.log('In setTheTheme(). NO THEME WAS SAVED SO LEAVING THE DEFAULT, .brushedAluminum.');
                displayAlertDialog('In setTheTheme(). NO THEME WAS SAVED SO LEAVING THE DEFAULT, .brushedAluminum.');
            } else {

                console.log('In setTheTheme(). THEME: ' + this.options.workflowAppTheme);

                var elements = $('.bwColoredMenuItem');
                var selectedButtonElement;
                for (var i = 0; i < elements.length; i++) {
                    for (var j = 0; j < elements[i].classList.length; j++) {
                        if (elements[i].classList[j].indexOf('_SelectedButton') > -1) { // All of our theme classes have [brushedAluminum] in the name. // brushedAluminum_orange_SelectedButton 
                            selectedButtonElement = elements[i];
                            break;
                        }
                    }
                }

                for (var i = 0; i < elements.length; i++) {

                    var classRemovalArray = [];
                    for (var j = 0; j < elements[i].classList.length; j++) {
                        if (elements[i].classList[j].indexOf('brushedAluminum') > -1) { // All of our theme classes have [brushedAluminum] in the name.
                            classRemovalArray.push(elements[i].classList[j]);
                        }
                    }
                    for (var k = 0; k < classRemovalArray.length; k++) {
                        elements[i].classList.remove(classRemovalArray[k]);
                    }

                    if (elements[i].classList.contains('noanimation')) {
                        elements[i].classList.add(this.options.workflowAppTheme + '_noanimation');
                    } else {
                        if (selectedButtonElement != elements[i]) {
                            elements[i].classList.add(this.options.workflowAppTheme); // Now that the old theme has been removed, add the selected theme.
                        }
                    }
                }

                selectedButtonElement.classList.add(this.options.workflowAppTheme + '_SelectedButton');

                $('.bwActiveMenu:first').bwActiveMenu('renderOutsideCurveInTheMenuBar'); // We re-render these here so that they are the correct color.
                $('.bwActiveMenu:first').bwActiveMenu('renderInsideCurveInTheMenuBar');

            }

        } catch (e) {
            console.log('Exception in bwAuthentication.js.setTheTheme(): ' + e.message + ', ' + e.stack);
            alert('Exception in bwAuthentication.js.setTheTheme(): ' + e.message + ', ' + e.stack);
        }
    },
