    logInAsTenantOrganizationOwner: function (elementId) {
        try {
            console.log('In bwAuthentication.js.logInAsTenantOrganizationOwner(). elementId: ' + elementId);
            alert('In bwAuthentication.js.logInAsTenantOrganizationOwner(). elementId: ' + elementId);
            var thiz = this;

            var selectedValue = $('#' + elementId + ' option:selected').val();
            var bwTenantId = selectedValue.split('|')[0];
            var rootOrganization = selectedValue.split('|')[1];

            console.log('In bwAuthentication.js.logInAsTenantOrganizationOwner(). elementId: ' + elementId + ', selectedValue: ' + selectedValue + ', bwTenantId: ' + bwTenantId + ', rootOrganization: ' + rootOrganization);

            //var proceed = confirm('Click Ok to log into "' + rootOrganization + '" as a tenant owner.');
            //if (proceed) {
            // Save the bwTenantId in the ForestAdministratorLoginToTenant table so that we don't expose any information and make this as secure as possible.

            console.log('Calling ForestAdministratorLoginToTenant');

            var workflowAppId = $('.bwAuthentication').bwAuthentication('option', 'workflowAppId');
            var participantId = $('.bwAuthentication').bwAuthentication('option', 'participantId');

            var activeStateIdentifier = $('.bwAuthentication:first').bwAuthentication('getActiveStateIdentifier');

            var data = {
                bwParticipantId_LoggedIn: participantId,
                bwActiveStateIdentifier: activeStateIdentifier,
                bwWorkflowAppId_LoggedIn: workflowAppId,

                bwTenantId: bwTenantId,
                bwWorkflowAppId: rootOrganization
            };
            var operationUri = this.options.operationUriPrefix + "_bw/ForestAdministratorLoginToTenant";
            $.ajax({
                url: operationUri,
                type: 'POST',
                data: data,
                headers: {
                    "Accept": "application/json; odata=verbose"
                },
                success: function (results) {

                    console.log('In bwAuthentication.js.logInAsTenantOrganizationOwner().success(). results: ' + JSON.stringify(results));

                    if (results.message != 'SUCCESS') {

                        console.log('In bwAuthentication.js.logInAsTenantOrganizationOwner().success(). ERROR: FAILED TO AUTHENTICATE: ' + JSON.stringify(results));
                        alert('ERROR: FAILED TO AUTHENTICATE: ' + results.message);

                    } else {

                        var operationUri = thiz.options.operationUriPrefix + '?ForestAdministratorLoginToTenant_ActiveStateIdentifier=' + results.bwActiveStateIdentifier;
                        console.log('Calling window.open(). operationUri: ' + operationUri);
                        var newWin = window.open(operationUri);

                        if (!newWin || newWin.closed || typeof newWin.closed == 'undefined') {
                            //POPUP BLOCKED
                            location.href = operationUri;
                        }

                    }
                },
                error: function (data, errorCode, errorMessage) {
                    console.log('Error in bwAuthentication.js.logInAsTenantOrganizationOwner().error(). Error in logInAsTenantOrganizationOwner(): ' + errorMessage);
                    alert('Error in bwAuthentication.js.logInAsTenantOrganizationOwner(): ' + errorMessage);
                }
            });

        } catch (e) {
            console.log('Exception in bwAuthentication.js.logInAsTenantOrganizationOwner(): ' + e.message + ', ' + e.stack);
            alert('Exception in bwAuthentication.js.logInAsTenantOrganizationOwner(): ' + e.message + ', ' + e.stack);
        }
    },











